package AB55;

public class SwitchCaseStatement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		switch (3)
		{
		case 1: System.out.println("Chrome Browser");
				//break;
	
		case 2:System.out.println("Firefox Browser");
				break;
			
		case 3: System.out.println("Safari Browser");
				//break;
			
		case 4:System.out.println("Edge Browser");
				break;
				
		default: System.out.println("Sorry that your selection is wrong");
		System.out.println("Press 1 for chrome browser");
		System.out.println("Press 2 for firefox browser");
		System.out.println("Press 3 for safari browser");
		System.out.println("Press 4 for edge browser");
		
		}
		
	}

}
